import {useState,useEffect} from 'react';
import {useNavigate} from 'react-router-dom';
import {useBlog} from '../middleware/contextHooks';
import {Transition} from 'react-transition-group';
import {LoremIpsum} from 'lorem-ipsum'
import {toast} from 'react-toastify';
import gsap from 'gsap' //used for animation
import {
    Grid, Slider, TextField, Container,
    Button, Paper, Stack, Typography,
    FormControlLabel, Checkbox
} from '@mui/material'

//-----------------------------Components--------------------------
import MainContainer from '../components/MainContainer';
//---------------------end region----------------------------------



export default function NewBlog(){
    const navigate = useNavigate()

    const [newBlog, setNewBlog] = useState({title:'',content: ''});
    const {toasts,clearErrors,createBlog,blogs,getBlogs} = useBlog();

    useEffect(() => {
        if(!blogs){
            getBlogs();
        }
        if(toasts){
            toasts.forEach(ele => {
                toast(ele.message, {type: ele.type})
            })
            clearErrors()
        }
    },[toasts,clearErrors,blogs,getBlogs]);
    return(
        <MainContainer>
            <Container maxWidth="md" sx={{py: 2, my: 1, backgroundColor: 'silver'}} component={Paper}>
                <Grid container spacing={2}></Grid>
            </Container>
        </MainContainer>
    )
}
