import {useState,useEffect} from 'react'
import MainContainer from '../components/MainContainer'
import { useParams } from 'react-router-dom'
import {
    Container, Paper,Button, TextField, Stack, IconButton,Typography
} from '@mui/material'
import {toast} from 'react-toastify'

// ---------------------------Icons----------------------
import DeleteForeverIcon from '@mui/icons-material/DeleteForever'
import EditIcon from '@mui/icons-material/Edit'
//------------------ end region--------------------------

import { useBlog } from '../middleware/contextHooks' 

export default function BlogDetail(){
    const {id} = useParams();
    const {currentBlog, getBlogById, toasts, clearToasts} = useBlog();

    const [edit,setEdit] = useState(false); //for creating a disabled state
    const [temp, setTemp] = useState(null); //temporary blog

    const [blog,setBlog] = useState(null);

    useEffect(() =>{
        if(!currentBlog || currentBlog?._id!==id){
            getBlogById(id);
        }

        if(currentBlog?._id === id){
            setBlog(currentBlog);
        }


        // if(toasts){
        //     toasts.forEach(ele => {
        //         toast(ele.message, {type: ele.type})
        //     })
        // }

    }, [currentBlog, id, toasts, clearToasts, getBlogById])

    const handleDelete = () => {}

    const handleEdit = () => {
        setEdit(true);
        setTemp(blog); //creating temp copy of our blog, so whatever state our blog is right now we're going to create a temp copy of it
    }

    const handleCancel = () => {
        setEdit(false);
        setBlog
    }


    const displayDisabled = () => {
        return(
            <Stack spacing={2}>
                <Stack spacing={2} direction='row'>
                    <Typography variant='h5' sx={{flowGrow: 1}}>{blog?.title}</Typography>
                    <IconButton onClick={handleEdit}>
                        <EditIcon/>
                    </IconButton>
                    <IconButton onClick={handleDelete}>
                        <DeleteForeverIcon/>
                    </IconButton>
                </Stack>
                <Typography variant='p'>{blog?.content}</Typography>
            </Stack>
        )
    }

    return(
        <MainContainer>
            <Container maxWidth='md' sx={{mt:3,mb:5}}>
                <Paper>
                    <Stack spacing={2}>
                        <TextField 
                            label='Title' name='title'
                            value={blog?.title} //if blog is not null the value is title
                            onChange={(e) => setBlog({...blog, title: e.target.value})}
                        />
                        <TextField 
                            label='Content' name='content'
                            value={blog?.content}
                            onChange={(e) => setBlog({...blog, content: e.target.value})}
                            multiline minRows={5} maxRows={20}
                        />
                        <Stack spacing={2} direction='row'>
                            <Button variant='contained'>Save</Button> 
                            <Button variant='outlined'>Cancel</Button> 
                        </Stack>
                    </Stack>
                </Paper>
            </Container>
        </MainContainer>
    )
}
